<?class oldboys_client extends TClientController {	public function oldboys($code_name){        #return [];        $this->content = $this->load_module($code_name);        $content = $this->core->sql->query("SELECT * FROM `dat_about` ORDER BY `priority`");        #if (empty($this->page) && $content->num_rows>1)        #	header("location: ./".$content->cell[1]['alias']);        $submenu = array();        for ($i=0; $i<$content->num_rows; $i++) {            if ($content->cell[$i]['alias'] == $this->page) {                $title = $content->cell[$i]['title'];                $text = $content->cell[$i]['text'];            }			if ($content->cell[$i]['alias'] != '_')				array_push($submenu, $content->cell[$i]['alias']);		}        /*        if (!isset($title) && !isset($text)) {			$this->content = $this->error404($code_name);			return $this->content;		}        */		$this->content['content'] = array(			'title' => $title,			'text' => func::correctMediaSize(func::setMaxImageSize(func::delEmptyParagraph(func::delShortTagText($text)), 676, 676, "0xfbf6ea:85"), 676)		);		$this->content['subclass'] = "inner about";		if (count($submenu)>0)			$this->content['submenu_tpl'] = "submenu.about.tpl";        #var_dump($this->content); die;        #/*		$this->content['content'] = array(			#'title' => 'Old Boys',		);		$this->content['headers']['title'] = 'OLD Boys';        #*/        unset(            $this->content['panels'],            $this->content['contact'],            $this->content['comments'],            $this->content['submenu_tpl'],            $this->content['cookie'],            $this->content['subclass'],            $this->content['__NULL__']        );        #echo '<pre>' . print_r($this->content, true) . '</pre>'; die;        		return $this->content;	}}?>